{
  "version": "0.2",
  "phases": {
    "pre_build": {
      "commands": [
        "env",
        "export TAG=${CODEBUILD_RESOLVED_SOURCE_VERSION}",
        "wget -O /usr/local/bin/kubectl https://storage.googleapis.com/kubernetes-release/release/v1.17.0/bin/linux/amd64/kubectl",
        "chmod +x /usr/local/bin/kubectl"
      ]
    },
    "build": {
      "commands": [
        "docker build -t $ECR_REPO_URI:$TAG .",
        "docker build -t $ECR_REPO_URI:latest .",
        "$(aws ecr get-login --no-include-email)",
        "docker push $ECR_REPO_URI:$TAG",
        "docker push $ECR_REPO_URI:latest"
      ]
    },
    "post_build": {
      "commands": [
        "aws eks update-kubeconfig --name microservice --verbose",
        "kubectl get no",
        "sed s%IMAGE_TAG_PLACEHOLDER%$ECR_REPO_URI:$TAG% k8s/adservice.yaml | kubectl -n microservice apply -f - --record"
      ]
    }
  }
}

